import './App.css'
import HelloWord from "./HelloWord"
import Button from "./Button"
import HelloPerson from "./HelloPerson";
import { useState } from 'react';

function App() {

    const [nome, setNome] = useState("")

    const pessoas = [
        {
            nome: 'Daniel',
            idade: 50,
            caracteristica: 'Maromba',
            foto:'data:image/jpeg;base64,'
        },
        {
            nome: 'Jorginho',
            idade: 25,
            caracteristica: 'Magro',
            foto:'https://ohoje.com/public/imagens/fotos/amp/c1bd81fb685d8fea5914503e96a8cc5b.jpg'
        },

        {
            nome: 'Paulo',
            idade: 17,
            caracteristica: 'Baixinho',
            foto:'http://3.bp.blogspot.com/-brzCyyDcY50/UOGolHHPJRI/AAAAAAAAEaA/K3pvP6x46M4/s1600/DSCF1365.JPG'

        },
        {
            nome: 'Sidnei',
            idade: 20,
            caracteristica: 'Feio',
            foto:'data:image/jpeg;base64,'

        }
    ]

    return (
        <div>
            <input type="text" onChange={(event) => { setNome(event.target.value) }} />
            <HelloWord />
            <Button />

            {pessoas.map((pessoa, key) => {

                return <HelloPerson 
                    key={key}
                    name={pessoa.nome}
                    idade={pessoa.idade}
                    caracteristica={pessoa.caracteristica}
                    foto={pessoa.foto}
                />
            })}

        </div>
    )
}

export default App
